package org.looping;

public class Whileloop {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
         
		int n=1;
		while(n<=5){
			System.out.print(n);
			n++;
		}
		System.out.println();
		System.out.println("__________________________________");
		
		
	int j=2;	
	do {
		System.out.print(j);
		j++;
	}
	while(j<=5);
	System.out.println();
	System.out.println("__________________________________");
	
	
		
		

} 
}

